mapper.select:
select distinct * from ( select row_number() over (order by code desc) rn, a.* from (select RANK() OVER (PARTITION BY phone.phone_code ORDER BY TO_CHAR(rental_end_date, 'YYYYMMDD') DESC, rental.rental_code DESC) AS RNK, phone.phone_code AS code, phone.phone_name AS name, phone.phone_maker AS maker, phone.phone_os AS os, phone.phone_version AS version, phone.phone_memory AS memory, phone.phone_display AS display, rental.rental_end_date AS enddate, rental.rental_status AS status from phone, rental where PHONE.PHONE_CODE = RENTAL.PHONE_CODE(+)
and phone_maker like #{maker}
and phone_name like '%' || #{name} || '%'
) a where rnk = '1') where rn between #{beginIndex} and #{endIndex} order by rn

select count(*) from phone where phone_code is not null
and phone_maker like #{maker}
and phone_name like '%' || #{name} || '%'
